import './botao.css'
import './Sessao'
import React from 'react'
import Axios from 'axios'
import { useState } from 'react'  

export default function Botao(props) {

    const [productList, setProductList] = useState<Array>([]);

    const addProduct = () => {
        console.log(props.name)
        Axios.post("http://localhost:3001/create", {
            nome: props.name,
            quantidade: props.amount,
            preco: props.preco,
            detalhes: props.details,
            descricao: props.description 
        }).then(() => {
            console.log('Success!!') 
            console.log(props.nome)
            
        })
    }

    const getProduct = () => {
        Axios.get("http://localhost:3001/products").then((response) => {
            console.log(response)
            setProductList(response.data); // retorna um Array com os dados da busca 
            console.log(response.data)
            console.log(productList)

        })
    }

    return (
        <div>
            <button onClick={props.op === 'addProduct'? addProduct : getProduct}> 
                {props.frase}
            </button>
            {props.op !== 'addProduct' ?
                <div>
                      {productList.map((valor, chave)=> {
                        return (
                            <div>{valor.name}</div>

                        ) 
                      })}   
                                     
                </div>  
             : 
             
             ( <div>   </div>) }
        </div>
    ) 
}

